mapper.select:

select name
		from participant
			where account_no = #{accountNo}

mapper.update:
update participant 
set account_change = #{accountChange}
where id = #{id} and account_no = #{accountNo}

update participant 
set transfer = #{transfer}
where id = #{id} and account_no = #{accountNo}

update participant 
set deposit_date = #{depositDate}
where id = #{id} and account_no = #{accountNo}
